import React, { useEffect, useState } from 'react';

const sectors = [
  'General',
  'Technology',
  'Finance',
  'Healthcare',
  'Education',
  'Marketing',
];

function CareerTips() {
  const [trends, setTrends] = useState([]);
  const [loading, setLoading] = useState(false);
  const [sector, setSector] = useState('General');
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchTrends() {
      setLoading(true);
      setError(null);

      try {
        const url =
          sector === 'General'
            ? 'http://localhost:8000/market-trends'
            : `http://localhost:8000/market-trends?sector=${encodeURIComponent(
                sector
              )}`;

        const res = await fetch(url);
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data = await res.json();

        if (Array.isArray(data.market_trends)) {
          setTrends(data.market_trends);
        } else {
          throw new Error('Unexpected data format');
        }
      } catch (err) {
        console.error('Failed to fetch market trends:', err);
        setError('Could not load trends. Please try again later.');
      } finally {
        setLoading(false);
      }
    }

    fetchTrends();
  }, [sector]);

  // Inline card styles
  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '20px',
    marginTop: '20px',
  };
  const cardStyle = {
    background: '#fff',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    borderLeft: '4px solid #4facfe',
  };
  const titleStyle = { margin: '0 0 10px', fontSize: '1.1em' };
  const descStyle = { margin: 0, lineHeight: 1.4 };

  return (
    <div className="career-tips-container">
      <h2>Career Tips & Market Trends</h2>

      <div style={{ margin: '15px 0' }}>
        <label htmlFor="sector-select">Select a sector: </label>
        <select
          id="sector-select"
          value={sector}
          onChange={(e) => setSector(e.target.value)}
          style={{ padding: '5px' }}
        >
          {sectors.map((s) => (
            <option key={s} value={s}>
              {s}
            </option>
          ))}
        </select>
      </div>

      {loading && <p>Loading trendsâ€¦</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {!loading && !error && (
        <div style={gridStyle}>
          {trends.map((t, i) => (
            <div key={i} style={cardStyle}>
              <h3 style={titleStyle}>{t.title}</h3>
              <p style={descStyle}>{t.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CareerTips;
