import React from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Button } from '@/components/ui/button'; // using shadcn/ui
import Recommendations from './components/Recommendations';
import ChatBot from './components/ChatBot';
import CareerTips from './components/CareerTips';

// Home component with hero section
function Home() {
  return (
    <motion.section
      className="relative flex flex-col items-center justify-center h-screen bg-gradient-to-br from-blue-600 to-purple-600 text-center text-white px-4"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <motion.h2
        className="text-4xl md:text-6xl font-bold mb-4 drop-shadow-lg"
        initial={{ y: -50 }}
        animate={{ y: 0 }}
        transition={{ delay: 0.3, duration: 0.8 }}
      >
        Welcome to the Future of Job Search
      </motion.h2>
      <motion.p
        className="text-lg md:text-2xl mb-8 max-w-2xl drop-shadow-sm"
        initial={{ y: 50 }}
        animate={{ y: 0 }}
        transition={{ delay: 0.5, duration: 0.8 }}
      >
        Discover personalized job recommendations tailored just for you with intelligent AI-powered insights.
      </motion.p>
      <motion.div
        initial={{ scale: 0.8 }}
        animate={{ scale: 1 }}
        transition={{ delay: 0.7, duration: 0.5 }}
      >
        <Button size="lg" className="rounded-2xl shadow-lg" as={Link} to="/recommendations">
          Get Started
        </Button>
      </motion.div>
    </motion.section>
  );
}

function App() {
  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        <header className="bg-white shadow-md">
          <nav className="container mx-auto flex items-center justify-between p-4">
            <Link to="/" className="text-2xl font-extrabold text-blue-600">
              AI JobRec
            </Link>
            <div className="space-x-6 hidden md:flex">
              <Link to="/" className="text-gray-700 hover:text-blue-600">
                Home
              </Link>
              <Link to="/recommendations" className="text-gray-700 hover:text-blue-600">
                Recommendations
              </Link>
              <Link to="/chat" className="text-gray-700 hover:text-blue-600">
                Chat
              </Link>
              <Link to="/career-tips" className="text-gray-700 hover:text-blue-600">
                Career Tips
              </Link>
            </div>
            {/* Mobile menu placeholder for future hamburger */}
          </nav>
        </header>

        <main className="flex-grow">
          <Switch>
            <Route path="/recommendations" component={Recommendations} />
            <Route path="/chat" component={ChatBot} />
            <Route path="/career-tips" component={CareerTips} />
            <Route path="/" exact>
              <Home />
            </Route>
          </Switch>
        </main>

        <footer className="bg-gray-100">
          <div className="container mx-auto text-center p-4 text-sm text-gray-600">
            Â© {new Date().getFullYear()} AI JobRec. All rights reserved.
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
