// src/components/CareerTips.js

import React, { useEffect, useState } from 'react';

const sectors = [
  'General',
  'Technology',
  'Finance',
  'Healthcare',
  'Education',
  'Marketing'
];

function CareerTips() {
  const [tips, setTips]       = useState([]);   
  const [loading, setLoading] = useState(false);
  const [sector, setSector]   = useState('General');
  const [error, setError]     = useState(null);

  useEffect(() => {
    async function fetchTips() {
      setLoading(true);
      setError(null);

      try {
        const base = 'http://localhost:8000/market-trends';
        const url  = sector === 'General'
          ? base
          : `${base}?sector=${encodeURIComponent(sector)}`;

        const res = await fetch(url);
        if (!res.ok) throw new Error(`Status ${res.status}`);
        const data = await res.json();

        // **Now we can assume** data.market_trends is a real array of { title, description }
        if (Array.isArray(data.market_trends)) {
          setTips(data.market_trends);
        } else {
          throw new Error('Unexpected format from backend');
        }
      } catch (err) {
        console.error("Failed to fetch tips:", err);
        setError("Unable to fetch market trends. Please try again later.");
      } finally {
        setLoading(false);
      }
    }

    fetchTips();
  }, [sector]);

  return (
    <div className="career-tips-container">
      <h2>Career Tips & Market Trends</h2>

      <div style={{ margin: '15px 0' }}>
        <label htmlFor="sector-select">Select a sector: </label>
        <select
          id="sector-select"
          value={sector}
          onChange={e => setSector(e.target.value)}
          style={{ padding: '5px' }}
        >
          {sectors.map(s => (
            <option key={s} value={s}>{s}</option>
          ))}
        </select>
      </div>

      {loading && <p>Loading tips...</p>}
      {error   && <p style={{ color: 'red' }}>{error}</p>}

      {!loading && !error && (
        <div className="jobs-grid">
          {tips.map((tip, idx) => (
            <div className="job-card" key={idx}>
              <h3>{tip.title}</h3>
              <p className="job-card-description">{tip.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CareerTips;
