import React, { useState } from 'react';

function Recommendations() {
  const [preferences, setPreferences] = useState('');
  const [skills, setSkills] = useState('');
  const [jobResults, setJobResults] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setJobResults([]); // Clear previous results
    setLoading(true);

    try {
      const response = await fetch('http://localhost:8000/recommendations', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ preferences, skills }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ message: `Status ${response.status}` }));
        throw new Error(errorData.message || `Status ${response.status}`);
      }

      const data = await response.json();
      setJobResults(Array.isArray(data.recommendations) ? data.recommendations : []);
    } catch (err) {
      console.error(err);
      setError(err.message || 'Failed to fetch recommendations. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const truncateText = (text, maxLength) => {
    if (!text) return 'No description provided.';
    return text.length > maxLength ? text.slice(0, maxLength) + '…' : text;
  };

  return (
    <div className="recommendations-container">
      <h2>Job Recommendations</h2>

      {error && <p className="error-message">{error}</p>}

      <form onSubmit={handleSubmit} className="form-container">
        <div>
          <label htmlFor="preferences-input">Preferences:</label>
          <input
            id="preferences-input"
            type="text"
            value={preferences}
            onChange={(e) => setPreferences(e.target.value)}
            placeholder="E.g., Remote, Full-time, San Francisco"
          />
        </div>

        <div>
          <label htmlFor="skills-input">Skills:</label>
          <input
            id="skills-input"
            type="text"
            value={skills}
            onChange={(e) => setSkills(e.target.value)}
            placeholder="E.g., React, Node.js, Python"
          />
        </div>

        <button type="submit" disabled={loading} className="btn-primary">
          {loading ? 'Loading…' : 'Get Recommendations'}
        </button>
      </form>

      {loading && <p className="loading-message">Fetching recommendations...</p>}

      {!loading && jobResults.length === 0 && !error && (
        <p className="no-results-message">
          No recommendations found. Try adjusting your preferences or skills!
        </p>
      )}

      {jobResults.length > 0 && (
        <div className="jobs-grid">
          {jobResults.map((job, index) => (
            <div key={index} className="job-card">
              {job.logo && (
                <div className="job-card-logo">
                  <img src={job.logo} alt={`${job.company || 'Company'} logo`} />
                </div>
              )}
              <div className="job-card-content">
                <h3>{job.title || 'Untitled Position'}</h3>
                <p className="job-company"><strong>Company:</strong> {job.company || 'Unknown'}</p>
                <p className="job-location"><strong>Location:</strong> {job.location || 'Not specified'}</p>
                <div className="job-card-description">
                  <p>{truncateText(job.short_description || job.description, 150)}</p>
                </div>
                {job.skills && Array.isArray(job.skills) && job.skills.length > 0 && (
                  <div className="job-skills">
                    <strong>Skills: </strong>
                    {job.skills.map((skill, idx) => (
                      <span key={idx} className="skill-badge">{skill}</span>
                    ))}
                  </div>
                )}
                {job.url && (
                  <a href={job.url} target="_blank" rel="noopener noreferrer" className="job-link">
                    View Posting
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Recommendations;
