// src/components/CareerTips.js

import React, { useEffect, useState } from 'react';

const sectors = [
  'General',
  'Technology',
  'Finance',
  'Healthcare',
  'Education',
  'Marketing'
];

function CareerTips() {
  const [tips, setTips]     = useState([]);
  const [loading, setLoading] = useState(false);
  const [sector, setSector]   = useState('General');
  const [error, setError]     = useState(null);

  useEffect(() => {
    async function fetchTips() {
      setLoading(true);
      setError(null);
      try {
        const base = 'http://localhost:8000/market-trends';
        const url  = sector === 'General'
          ? base
          : `${base}?sector=${encodeURIComponent(sector)}`;

        const res = await fetch(url);
        if (!res.ok) throw new Error(`Status ${res.status}`);
        const data = await res.json();

        // Directly assume array of {title, description}
        setTips(Array.isArray(data.market_trends) ? data.market_trends : []);
      } catch (err) {
        console.error("Failed to fetch tips:", err);
        setError("Unable to fetch market trends. Please try again later.");
      } finally {
        setLoading(false);
      }
    }

    fetchTips();
  }, [sector]);

  return (
    <div className="career-tips-container">
      <h2>Career Tips & Market Trends</h2>

      <div className="form-container">
        <label htmlFor="sector-select">Select a sector:</label>
        <select
          id="sector-select"
          value={sector}
          onChange={e => setSector(e.target.value)}
        >
          {sectors.map(s => (
            <option key={s} value={s}>{s}</option>
          ))}
        </select>
      </div>

      {loading && <p>Loading tipsâ€¦</p>}
      {error   && <p className="error">{error}</p>}

      {!loading && !error && (
        <div className="tips-grid">
          {tips.map((tip, idx) => (
            <div key={idx} className="tip-card">
              <h3>{tip.title}</h3>
              <p>{tip.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CareerTips;
