// src/App.js

import React from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';

import Recommendations from './components/Recommendations';
import ChatBot         from './components/ChatBot';
import CareerTips      from './components/CareerTips';

// Make sure this path matches where your CSS lives:
import './style.css';

function App() {
  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <h1>AI Job Recommendation System</h1>
          <p>Empowering your job search with intelligent recommendations and mentorship.</p>
          <nav className="navbar">
            <Link to="/">Home</Link>
            <Link to="/recommendations">Recommendations</Link>
            <Link to="/chat">Chat</Link>
            <Link to="/career-tips">Career Tips</Link>
          </nav>
        </header>

        <main className="app-main">
          <Switch>
            {/* Home / Hero */}
            <Route path="/" exact>
              <div className="home-content">
                <h2>Welcome to the Future of Job Search</h2>
                <p>Discover personalized job recommendations tailored for you.</p>
                <Link
                  to="/recommendations"
                  style={{
                    display:        'inline-block',
                    marginTop:      '20px',
                    padding:        '12px 24px',
                    background:     '#4facfe',
                    color:          '#fff',
                    textDecoration: 'none',
                    borderRadius:   '4px',
                    boxShadow:      '0 4px 6px rgba(0,0,0,0.1)'
                  }}
                >
                  Get Started
                </Link>
              </div>
            </Route>

            {/* Recommendations page */}
            <Route path="/recommendations" component={Recommendations} />

            {/* ChatBot page */}
            <Route path="/chat" component={ChatBot} />

            {/* Career Tips page */}
            <Route path="/career-tips" component={CareerTips} />

          </Switch>
        </main>

        <footer className="app-footer">
          <p>Â© {new Date().getFullYear()} AI Job Recommendation System. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
