import React, { useEffect, useState } from 'react';
import ReactMarkdown from 'react-markdown';

function CareerTips() {
  const [tips, setTips] = useState([]);

  useEffect(() => {
    async function fetchTips() {
      try {
        const response = await fetch('http://localhost:8000/market-trends');
        const data = await response.json();
        const trends = Object.entries(data.market_trends).map(([sector, trend]) => `${sector}: ${trend}`);
        setTips(trends);
      } catch (err) {
        console.error("Failed to fetch tips:", err);
      }
    }
    fetchTips();
  }, []);

  return (
    <div className="career-tips-container">
      <h2>Career Tips & Market Trends</h2>
      <ul className="tips-list">
        {tips.map((tip, index) => (
          <li key={index}>
            <ReactMarkdown>{tip}</ReactMarkdown>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CareerTips;
