import React from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import './style.css';
import Recommendations from './components/Recommendations';
import ChatBot from './components/ChatBot';
import CareerTips from './components/CareerTips';

// Home component styled via style.css
function Home() {
  return (
    <div className="home-content">
      <h2>Welcome to the Future of Job Search</h2>
      <p>Discover personalized job recommendations tailored just for you with intelligent AI-powered insights.</p>
      <Link
        to="/recommendations"
        style={{
          display: 'inline-block',
          marginTop: '20px',
          padding: '12px 24px',
          background: '#fff',
          color: '#4facfe',
          borderRadius: '4px',
          fontWeight: 'bold',
          textDecoration: 'none',
          boxShadow: '0px 4px 8px rgba(0,0,0,0.1)',
          transition: 'background 0.3s ease',
        }}
        onMouseEnter={e => e.currentTarget.style.background = '#f4f7f9'}
        onMouseLeave={e => e.currentTarget.style.background = '#fff'}
      >
        Get Started
      </Link>
    </div>
  );
}

function App() {
  return (
    <div className="app-container">
      <Router>
        <header className="app-header">
          <h1>AI Job Recommendation System</h1>
          <p>Empowering your job search with intelligent recommendations and mentorship.</p>
          <nav className="navbar">
            <Link to="/">Home</Link>
            <Link to="/recommendations">Recommendations</Link>
            <Link to="/chat">Chat</Link>
            <Link to="/career-tips">Career Tips</Link>
          </nav>
        </header>

        <main className="app-main">
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/recommendations" component={Recommendations} />
            <Route path="/chat" component={ChatBot} />
            <Route path="/career-tips" component={CareerTips} />
          </Switch>
        </main>

        <footer className="app-footer">
          <p>Â© {new Date().getFullYear()} AI Job Recommendation System. All rights reserved.</p>
        </footer>
      </Router>
    </div>
  );
}

export default App;
