import React, { useState, useRef, useEffect } from 'react'; // Added useRef and useEffect for optional scroll
import ReactMarkdown from 'react-markdown';

function ChatBot() {
  const [message, setMessage] = useState('');
  const [chatLog, setChatLog] = useState([]);
  // Optional: Add state for loading if you implement it
  // const [isLoading, setIsLoading] = useState(false);

  const chatWindowRef = useRef(null); // Ref for the chat window for scrolling

  // Optional: Effect to scroll to the bottom when chatLog updates
  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [chatLog]);

  const sendMessage = async (e) => {
    e.preventDefault();
    const userMessage = message.trim(); // Trim whitespace
    if (!userMessage) return; // Prevent sending empty messages

    // Add user message to log immediately
    setChatLog(prevChatLog => [...prevChatLog, { sender: 'User', text: userMessage }]);
    setMessage(''); // Clear input field right away
    // Optional: Set loading state
    // setIsLoading(true);

    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userMessage }), // Use trimmed message
      });

      // Optional: Set loading state to false on response (or error)
      // setIsLoading(false);

      if (!response.ok) {
         // Attempt to read error body if available, otherwise use status text
         const errorBody = await response.text().catch(() => response.statusText);
         throw new Error(`HTTP error! Status: ${response.status} - ${errorBody}`);
      }

      const data = await response.json();
      // Add bot response to log
      setChatLog(prevChatLog => [...prevChatLog, { sender: 'Bot', text: data.response }]);

    } catch (error) {
      console.error("Error sending message:", error);
      // Optional: Set loading state to false on error
      // setIsLoading(false);
      // Add an error message to the chat log
      setChatLog(prevChatLog => [...prevChatLog, { sender: 'Bot', text: `*Error: Could not get a response. Please try again later.*` }]); // Use markdown for italics
    }
  };


  return (
    <div className="chatbot-container">
      <h2>Chat with AI Mentor</h2>
      {/* Assign the ref to the chat window */}
      <div className="chat-window" ref={chatWindowRef}>
        {/* Updated initial message */}
        {chatLog.length === 0 && (
            <div className="chat-entry bot" style={{ alignSelf: 'center', textAlign: 'center', maxWidth: '85%', margin: '1rem auto' }}>
               <strong>Bot:</strong> Hello! I'm your AI Mentor, here to help you with training resources and finding suitable job opportunities. What can I assist you with today?
            </div>
        )}
        {/* Render chat messages */}
        {chatLog.map((entry, idx) => (
          // Added key prop for list rendering performance
          <div key={idx} className={`chat-entry ${entry.sender.toLowerCase()}`} data-is-markdown={entry.sender === 'Bot' ? "true" : undefined}>
            <strong>{entry.sender}:</strong> <ReactMarkdown>{entry.text}</ReactMarkdown>
          </div>
        ))}
        {/* Optional: Add a loading indicator message */}
        {/* {isLoading && (
            <div className="chat-entry bot loading-indicator" style={{ alignSelf: 'flex-start', maxWidth: 'max-content', animation: 'pulse 1.5s infinite ease-in-out' }}>
               <strong>Bot:</strong> ...
            </div>
        )} */}
      </div>
      <form onSubmit={sendMessage} className="chat-form">
        {/* Assigned the class name "chat-input" */}
        <input
          className="chat-input"
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message here"
          // Optional: Disable input while waiting for response
          // disabled={isLoading}
        />
        {/* Optional: Disable button while waiting for response or if input is empty */}
        <button type="submit" disabled={!message.trim() /* || isLoading */}>Send</button>
      </form>
       {/* Optional loading spinner/message outside the chat form if preferred */}
       {/* {isLoading && <div className="loading-message">Sending...</div>} */}
    </div>
  );
}

export default ChatBot;
