import React, { useEffect, useState } from 'react';
import ReactMarkdown from 'react-markdown';

const sectors = [
  'General',
  'Technology',
  'Finance',
  'Healthcare',
  'Education',
  'Marketing'
];

function CareerTips() {
  const [tips, setTips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [sector, setSector] = useState('General');
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchTips() {
      setLoading(true);
      setError(null);

      try {
        const url =
          sector === 'General'
            ? 'http://localhost:8000/market-trends'
            : `http://localhost:8000/market-trends?sector=${encodeURIComponent(sector)}`;

        const res = await fetch(url);
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data = await res.json();

        // Extract only the markdown bullets with bolded headlines
        const lines =
          typeof data.market_trends === 'string'
            ? data.market_trends
                .split('\n')
                .map((l) => l.trim())
                .filter((l) => l.startsWith('- **'))
            : [];

        setTips(lines);
      } catch (err) {
        console.error('Failed to fetch tips:', err);
        setError('Unable to fetch market trends. Please try again later.');
      } finally {
        setLoading(false);
      }
    }

    fetchTips();
  }, [sector]);

  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '20px',
    marginTop: '20px'
  };

  const cardStyle = {
    background: '#fff',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    borderLeft: '4px solid #4facfe'
  };

  return (
    <div className="career-tips-container">
      <h2>Career Tips & Market Trends</h2>

      <div style={{ margin: '15px 0' }}>
        <label htmlFor="sector-select">Select a sector: </label>
        <select
          id="sector-select"
          value={sector}
          onChange={(e) => setSector(e.target.value)}
          style={{ padding: '5px' }}
        >
          {sectors.map((s) => (
            <option key={s} value={s}>
              {s}
            </option>
          ))}
        </select>
      </div>

      {loading && <p>Loading trendsâ€¦</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {!loading && !error && (
        <div style={gridStyle}>
          {tips.map((tip, idx) => (
            <div key={idx} style={cardStyle}>
              <ReactMarkdown>{tip}</ReactMarkdown>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CareerTips;
